import { Component } from '@angular/core';
import { Product } from '../models/product.model';
import { CommonModule } from '@angular/common';
import { CartService } from '../services/cart.service';
import { SidebarComponent } from "../components/sidebar/sidebar.component";

@Component({
  selector: 'app-products',
  imports: [CommonModule, SidebarComponent],
  templateUrl: './products.component.html',
  styleUrl: './products.component.css'
})
export class ProductsComponent {

  products: Product[] = [
    { id: 1, name: 'Burger', type: 'burger', price: 1, image: 'data:image/jpeg;base64,' },
    { id: 2, name: 'Pizza', type: 'pizza', price: 2, image: 'data:image/jpeg;base64,' },
    { id: 3, name: 'Soda', type: 'drink', price: 3, image: 'data:image/jpeg;base64,' }
  ];

  constructor(private cartService: CartService) {}

  addToCart(product: Product) {
    this.cartService.addToCart(product);
    alert(`${product.name} added to cart!`);
  }

  filteredProducts: Product[] = [];

ngOnInit() {
  this.filteredProducts = this.products;
}

applyFilter(filter: { type: string; price: number }) {
  this.filteredProducts = this.products.filter(p => {
    const typeMatch = !filter.type || p.name.toLowerCase().includes(filter.type.toLowerCase());
    const priceMatch = p.price <= filter.price;
    return typeMatch && priceMatch;
  });
}

}
